def key_ceremony_joint_election_key_step(election, _evaluator)
  joint_election_key_message = JOINT_ELECTION_KEY_MESSAGE.deep_dup
  joint_election_key_message["iat"] = Time.current.to_i
  joint_election_key_message["message_id"] = "#{election.unique_id}.key_ceremony.joint_election_key+b.#{BulletinBoard.unique_id}"

  election.log_entries << FactoryBot.build(:log_entry, election: election, bulletin_board: true,
                                                       signed_data: BulletinBoard.sign(joint_election_key_message), message: joint_election_key_message)
end
